    protected async void NextPage()
    {
        int MaxPage = Convert.ToInt32(Math.Round(Convert.ToDouble(_orders.Count / 3)) + 1);
        if (CurPage < MaxPage)
        {
            CurPage++;
            await GetOrders(0, CurPage - 1);
